import react, { useState } from 'react'
import "./selectitm.css"


const imgall=
{
    mobile:["./slct/1.png","./slct/2.png","./slct/3.png","./slct/7.png"],
    game:["./slct/1.png","./slct/2.png","./slct/7.png","./slct/7.png",],
    frontend:["./slct/1.png","./slct/2.png","./slct/7.png","./slct/7.png",],
    database:["./slct/1.png","./slct/2.png","./slct/7.png","./slct/7.png",],
    cms:["./slct/1.png","./slct/2.png","./slct/7.png","./slct/7.png",]


}


const Selectitm=()=>{

const [name,set]=useState([])

const change=(e)=>{
    set(e)
}

    return(<>
    
    
  <div className="container">
  <div className="col">
    <div className="row d-flex justify-content-center maintec">Technology</div>
    <div className="row d-flex justify-content-center m-0 listbox">
      <ul className="d-flex flex-row m-0">
        <li className="p-4 mb-0 hili" onClick={()=>{change('mobile')}}  >Mobile</li>
        <li className="p-4 mb-0 hili" onClick={()=>{change('game')}}>Game</li>
        <li className="p-4 mb-0 hili" onClick={()=>{change('frontend')}}>Front End</li>
        <li className="p-4 mb-0 hili" onClick={()=>{change('database')}}>Database</li>
        <li className="p-4 mb-0 hili" onClick={()=>{change('cms')}}>CMS</li>
      </ul>
    </div>
    <div className="row d-flex justify-content-center mt-4">

      
    {imgall.mobile.map((val,index)=>{
            return(
      <img src={val} className="imgicon " alt="img" key={index} />

            )
        })}



    </div>
  </div>
</div>

    
    
    
    
    </>)


}

export default Selectitm
