import react from "react"
import './css/cursor.css'


const Cursor=()=>{
    const s = document.getElementById("corser")
    document.addEventListener('mousemove', (e) => {
        s.setAttribute('style', "top:" + (e.pageY ) + "px; left:" + (e.pageX) + "px;")
    })

    return(<>
        
        
        <div id="corser"></div>
    
    
    </>)
}


export default Cursor












